function getRadianAngle(a){return a*Math.PI/180}function canvas(a){this.data=$(a).get(0);this.x=0;this.y=0;this.setAngle=function(b){this.angle=b};this.setPosition=function(b,c){this.x=b;this.y=c};this.setHeight=function(b){this.data.height=b};this.setWidth=function(b){this.data.width=b};this.saveImg=function(c){var b=$(c).get(0);this.img=b};this.getContext=function(){this.context=this.data.getContext("2d")};this.draw=function(b,d,e){if(e){var c=$(e).get(0)}else{var c=this.img}this.context.drawImage(c,this.x,this.y,b,d)};this.drawRotate=function(b,c){this.context.drawImage(this.img,-b/2,-c/2,b,c)};this.rotate=function(b,c){this.context.translate(b/2+this.x,c/2+this.y);this.context.rotate(getRadianAngle(this.angle))};this.joinImages=function(e,b,d){var c=$(e).get(0);this.context.drawImage(c,0,0,b,d)};this.clearRect=function(){this.context.clearRect(0,0,this.data.width,this.data.height)};this.resetTranslate=function(){this.context.setTransform(1,0,0,1,0.5,0.5)};this.save=function(){this.context.save()};this.restore=function(){this.context.restore()}};