function createPointIndicator(k){var p=new PIXI.Container();var l=k.toString();var o=l.split("");var m=new PIXI.Sprite.fromImage("asset/red_plus_sign.png");for(var i=0;i<o.length;i++){var n=new PIXI.Sprite.fromImage("asset/numberRed/"+o[i]+".png");var j=n.width*o.length;n.position.x=n.width*i-j/2;n.position.y=-n.height/2;p.addChild(n);m.position.x=-j/2-m.width;m.position.y=-m.height/2}p.addChild(m);return p}function animatePointIndicator(){TweenMax.killTweensOf(pointIndicator);pointIndicator.position.x=pointIndicator.x0;pointIndicator.position.y=pointIndicator.y0;pointIndicator.alpha=1;TweenMax.to(pointIndicator.position,0.7,{y:pointIndicator.y0-pointIndicator.height*2,ease:Sine.easeOut});TweenMax.to(pointIndicator,0.3,{alpha:0,ease:Sine.easeOut,delay:0.5})}function addCandyEffectAt(k,j){var i=PIXI.Sprite.fromImage("asset/cirEffect.png");i.blendMode=PIXI.BLEND_MODES.OVERLAY;stage.addChild(i);i.anchor.x=i.anchor.y=0.5;i.scale.x=0;i.scale.y=0;i.position.x=k;i.position.y=j;for(var n=0;n<5;n++){var h=PIXI.Sprite.fromImage("asset/flower.png");stage.addChild(h);h.anchor.x=h.anchor.y=0.5;h.position.x=k;h.position.y=j;h.scale.x=h.scale.y=0.5+0.5*Math.random();h.alpha=0.5;var l=100*(Math.random()-Math.random());var m=100*(Math.random()-Math.random());TweenMax.to(h.position,0.5,{x:k+l,y:j+m,ease:Cubic.easeOut,delay:0.1});TweenMax.to(h,0.3,{alpha:0,ease:Sine.easeOut,delay:0.2});TweenMax.to(h,0.5,{rotation:Math.PI/2,ease:Cubic.easeOut,delay:0.1})}TweenMax.to(i.scale,0.5,{x:1,y:1,ease:Cubic.easeOut,onCompleteParams:[i],onComplete:removeSpriteEffect});TweenMax.to(i,0.3,{alpha:0,ease:Sine.easeOut,delay:0.1})}function removeSpriteEffect(b){stage.removeChild(b);b=null};